import { useState,useEffect } from 'react';
import React from 'react';
import CourseDetails from '../components/courseDetails';

const FilterInstCourses =()=> {
  const [courses,setCourses] = useState(null);
  const [filter, setFilter] = useState('')
  const [error,setError] = useState(null)
  const [subject, setSubject] = useState('')
  const [price, setPrice] = useState('')

  useEffect(() => {
    const fetchCourses = async () => {
      const response = await fetch('/api/instructor/courses')
      const json = await response.json()

    if(response.ok){
        setCourses(json)
    }
    }
    fetchCourses();
}, []);
const handleSubmit = async(e) => {  
  // const user = {filter}  
  e.preventDefault();
  const inputVal= {subject:subject,price:price} //subject:subject,price:price
  if(filter!=='' ){ 
    const response = await fetch(`/api/instructor/filter?${new URLSearchParams(inputVal).toString()}`)
    const json = await response.json()
if(!response.ok){
    setError(json.error)
}
if(response.ok){
  setCourses(json); 
  console.log(json)
  setError(null)
  setFilter(null)
}
    }
  }
    return (
        <div className="InstCourses">
        <div className="courses">
        {courses && courses.map((course) => (
            <CourseDetails key={course._id} course={course} />
            ))}
        </div>
        <form className="filter" onSubmit={handleSubmit}>
        <input 
        type={"text"}
        placeholder="filter by subject or price"
        onChange={(e)=>  setSubject(e.target.value)  &&
        setPrice(e.target.value) 
        }/>
        <button onChange={(e) => setCourses(courses)}>Filter</button>
        {error && <div className="error">{error}</div>}
</form>
</div>
    );
}
export default FilterInstCourses;
