import {useEffect,useState} from 'react';
import React from 'react';
//components 
import CourseDetails from '../components/courseDetails';

const ItHome = () => {
    const [courses,setCourses] = useState(null);
    const [price, setPrice] = useState('')
    const [error,setError] = useState(null)
    const [subject, setSubject] = useState('')
    const [rating, setRating] = useState('')

useEffect(() => {
    const fetchCourses = async () => {
      const response = await fetch('/api/courses/getCourses')
      const json = await response.json()

    if(response.ok){
        setCourses(json)
    }
    }
    fetchCourses();
}, []);
const handleSubmit = async(e) => {  
    const priceVal = {price:price}
    const inputVal= {subject:subject,rating:rating}
    if (price!==''){//price filter
    e.preventDefault();
    const response = await fetch(`/api/courses/filterbyprice?${new URLSearchParams(priceVal).toString()}`)
    const json = await response.json()
if(!response.ok){
    setError(json.error)
}
if(response.ok){
  setCourses(json); 
  setError(null)
  setPrice('')
    }
}
if(subject==='' || rating===''){ //or
    const response= await fetch(`/api/courses/getcoursebysubjectorRating?${new URLSearchParams(inputVal).toString()}`)
    const json = await response.json()
    if(!response.ok){
        setError(json.error)
    }
    if(response.ok){
      setCourses(json);
      console.log(json)
      setError(null) 
      setSubject('')
      setRating('')
    }
}
else{ //and 
    const response= await fetch(`/api/courses/getcoursebysubjectRating?${new URLSearchParams(inputVal).toString()}`)
    const json = await response.json()
    if(!response.ok){
        setError(json.error)
    }
    if(response.ok){
      setCourses(json);
      console.log(json)
      setError(null)
      setSubject('')
      setRating('')
    }
}
}
    return (
        <div className="ItHome">
        <div className="courses">
            {courses && courses.map((course) => (
            <CourseDetails key={course._id} course={course} />
            ))}
        </div>
        <form className="pricefilter" onSubmit={handleSubmit}>
        <input 
        type={"text"}
        placeholder="filter by price"
        onChange={(e)=>setPrice(e.target.value)}/>
        <button onChange={(e) => setCourses(courses)}
        >Filter by Price </button>
        {error && <div className="error">{error}</div>}
</form>
<form className="subjectratingfilter" onSubmit={handleSubmit}>
        <input className="subjectfilter" 
        type={"text"}
        placeholder="filter by subject"
        onChange={(e)=>setSubject(e.target.value)}/>
         <input  className="ratingfilter" 
        type={"text"}
        placeholder="filter by rating"
        onChange={(e)=>setRating(e.target.value)}/>
        <button>Filter</button>
        {error && <div className="error">{error}</div>}
</form>
        </div>
    );
            }
export default ItHome;
